CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `receipt_view`  AS SELECT `rece`.`receipt_id` AS `receipt_id`, `rece`.`reservation_id` AS `reservation_id`, `cl`.`client_id` AS `client_id`, concat(`cl`.`client_name`,' ',`cl`.`client_surname`) AS `client_fullName`, `cl`.`client_DNI` AS `client_DNI`, `cl`.`client_email` AS `client_email`, `cl`.`client_phone` AS `client_phone`, `cl`.`credit_card` AS `credit_card`, `cl`.`client_country` AS `client_country`, `rece`.`check_in` AS `check_in`, `rece`.`check_out` AS `check_out`, to_days(`rece`.`check_out`) - to_days(`rece`.`check_in`) AS `Days_in_hotel`, `rece`.`Type` AS `type`, `rece`.`room_id` AS `room_id`, json_value(json_extract(`rece`.`services`,'$.Services.Gym'),'$.price') AS `Gym`, json_value(json_extract(`rece`.`services`,'$.Services.Spa'),'$.price') AS `Spa`, json_value(json_extract(`rece`.`services`,'$.Services.Bar'),'$.price') AS `Bar`, json_value(json_extract(`rece`.`services`,'$.Services.Wifi'),'$.price') AS `Wifi`, json_value(json_extract(`rece`.`services`,'$.Services.Disco'),'$.price') AS `Disco`, json_value(json_extract(`rece`.`services`,'$.Services.Arcade'),'$.price') AS `Arcade`, `rece`.`initial_price` AS `initial_price`, ifnull(json_value(json_extract(`rece`.`services`,'$.Services.Gym'),'$.price'),0) + ifnull(json_value(json_extract(`rece`.`services`,'$.Services.Spa'),'$.price'),0) + ifnull(json_value(json_extract(`rece`.`services`,'$.Services.Bar'),'$.price'),0) + ifnull(json_value(json_extract(`rece`.`services`,'$.Services.Wifi'),'$.price'),0) + ifnull(json_value(json_extract(`rece`.`services`,'$.Services.Disco'),'$.price'),0) + ifnull(json_value(json_extract(`rece`.`services`,'$.Services.Arcade'),'$.price'),0) * `type_multiplaier`(`rece`.`Type`) + ifnull(`rece`.`initial_price`,0) AS `total_price` FROM (`034_receipt` `rece` join `034_clients` `cl` on(`cl`.`client_id` = `rece`.`id_client_master`)) ;

